import React, { useState, useEffect } from 'react'
import { uploadFile } from '../client'
import SubtitlePreview from './SubtitlePreview'
import styles from './FileUploader.module.css'
import { translateSRT } from '../client'

function FileUploader() {
  const [isLoading, setIsLoading] = useState(false)
  const [message, setMessage] = useState('')
  const [selectedFile, setSelectedFile] = useState(null)
  const [fileContent, setFileContent] = useState('')
  const [targetLanguage, setTargetLanguage] = useState('he')
  const [translatedContent, setTranslatedContent] = useState('')
  const [translationProgress, setTranslationProgress] = useState(0)
  const [detectedLanguage, setDetectedLanguage] = useState('')

  const languages = {
    'af': 'אפריקאנס',
    'ar': 'ערבית',
    'bg': 'בולגרית',
    'bn': 'בנגלית',
    'bs': 'בוסנית',
    'ca': 'קטלאנית',
    'cs': 'צ׳כית',
    'cy': 'וולשית',
    'da': 'דנית',
    'de': 'גרמנית',
    'el': 'יוונית',
    'en': 'אנגלית',
    'es': 'ספרדית',
    'et': 'אסטונית',
    'fa': 'פרסית',
    'fi': 'פינית',
    'fr': 'צרפתית',
    'he': 'עברית',
    'hi': 'הינדי',
    'hr': 'קרואטית',
    'hu': 'הונגרית',
    'id': 'אינדונזית',
    'is': 'איסלנדית',
    'it': 'איטלקית',
    'ja': 'יפנית',
    'ko': 'קוריאנית',
    'lt': 'ליטאית',
    'lv': 'לטבית',
    'ms': 'מלאית',
    'mt': 'מלטזית',
    'nl': 'הולנדית',
    'no': 'נורווגית',
    'pl': 'פולנית',
    'pt': 'פורטוגזית',
    'ro': 'רומנית',
    'ru': 'רוסית',
    'sk': 'סלובקית',
    'sl': 'סלובנית',
    'sv': 'שוודית',
    'sw': 'סווהילי',
    'th': 'תאית',
    'tr': 'טורקית',
    'uk': 'אוקראינית',
    'ur': 'אורדו',
    'vi': 'וייטנאמית',
    'zh-Hans': 'סינית פשוטה',
    'zh-Hant': 'סינית מסורתית'
  }

  const detectLanguage = async (text) => {
    try {
      const response = await fetch(`${process.env.REACT_APP_AZURE_TRANSLATOR_ENDPOINT}/detect?api-version=3.0`, {
        method: 'POST',
        headers: {
          'Ocp-Apim-Subscription-Key': process.env.REACT_APP_AZURE_TRANSLATOR_KEY,
          'Ocp-Apim-Subscription-Region': process.env.REACT_APP_AZURE_REGION,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify([{ text }])
      });

      if (!response.ok) {
        throw new Error('שגיאה בזיהוי השפה');
      }

      const data = await response.json();
      return data[0].language;
    } catch (error) {
      console.error('שגיאה בזיהוי השפה:', error);
      return null;
    }
  }

  const handleFileUpload = async (event) => {
    const file = event.target.files[0]
    if (file) {
      setSelectedFile(file)
      setMessage('')
      setIsLoading(true)
      setTranslatedContent('')
      setDetectedLanguage('')
      
      try {
        const content = await readFileContent(file)
        setFileContent(content)
        
        const detectedLang = await detectLanguage(content)
        if (detectedLang) {
          setDetectedLanguage(detectedLang)
          setMessage(`הקובץ הועלה בהצלחה! שפה מזוהה: ${languages[detectedLang] || detectedLang}`)
        } else {
          setMessage('הקובץ הועלה בהצלחה!')
        }
        
        await uploadFile(file)
      } catch (error) {
        setMessage('שגיאה בהעלאת הקובץ: ' + error.message)
      } finally {
        setIsLoading(false)
      }
    }
  }

  const readFileContent = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader()
      reader.onload = (e) => resolve(e.target.result)
      reader.onerror = (e) => reject(e)
      reader.readAsText(file)
    })
  }

  const handleTranslate = async () => {
    if (!fileContent) return;
    
    setIsLoading(true);
    setTranslationProgress(0);
    
    try {
      const translated = await translateSRT(fileContent, targetLanguage, (progress) => {
        setTranslationProgress(progress);
      });
      
      setTranslatedContent(translated);
      setMessage(`תורגם בהצלחה ל${languages[targetLanguage]}`);
    } catch (error) {
      setMessage('שגיאה בתרגום: ' + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleDownload = () => {
    if (!translatedContent) return;

    const blob = new Blob([translatedContent], { type: 'text/plain;charset=utf-8' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = selectedFile ? `translated_${selectedFile.name}` : 'translated.srt';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  return (
    <div className={styles.fileUploader}>
      <input 
        type="file" 
        accept=".srt"
        onChange={handleFileUpload}
        disabled={isLoading}
      />
      
      {fileContent && (
        <div>
          {detectedLanguage && (
            <div className={styles.detectedLanguage}>
              שפה מזוהה: {languages[detectedLanguage] || detectedLanguage}
            </div>
          )}
          <select
            className={styles.select}
            value={targetLanguage}
            onChange={(e) => setTargetLanguage(e.target.value)}
            disabled={isLoading}
          >
            {Object.entries(languages)
              .filter(([code]) => code !== detectedLanguage)
              .map(([code, name]) => (
                <option key={code} value={code}>{name}</option>
              ))}
          </select>
          
          <button 
            onClick={handleTranslate}
            disabled={isLoading || targetLanguage === detectedLanguage}
            className={styles.translateButton}
          >
            {isLoading ? 'מתרגם...' : 'תרגם'}
          </button>
        </div>
      )}

      {isLoading && translationProgress > 0 && (
        <div className={styles.progressContainer}>
          <div 
            className={styles.progressBar}
            style={{ width: `${translationProgress}%` }}
          >
            {translationProgress}%
          </div>
        </div>
      )}

      {message && <div className={styles.message}>{message}</div>}

      {translatedContent && !isLoading && (
        <button 
          onClick={handleDownload}
          className={styles.downloadButton}
        >
          הורד קובץ מתורגם
        </button>
      )}

      {translatedContent && (
        <SubtitlePreview content={translatedContent} />
      )}
    </div>
  )
}

export default FileUploader
