import React, { useState, useRef } from 'react';
import { useApp } from '../context/AppContext';
import styles from './FileUploader.module.css';
import { FiUploadCloud, FiDownload, FiGlobe, FiMoon, FiSun } from 'react-icons/fi';
import { motion, AnimatePresence } from 'framer-motion';

function FileUploader() {
  const { t, interfaceLanguage, isDarkMode, changeLanguage, toggleTheme } = useApp();
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileContent, setFileContent] = useState('');
  const [targetLanguage, setTargetLanguage] = useState('he');
  const [translatedContent, setTranslatedContent] = useState('');
  const [translationProgress, setTranslationProgress] = useState(0);
  const [detectedLanguage, setDetectedLanguage] = useState('');
  const fileInputRef = useRef(null);

  const languages = {
    'af': 'אפריקאנס',
    'ar': 'ערבית',
    'bg': 'בולגרית',
    'bn': 'בנגלית',
    'bs': 'בוסנית',
    'ca': 'קטלאנית',
    'cs': 'צ׳כית',
    'cy': 'וולשית',
    'da': 'דנית',
    'de': 'גרמנית',
    'el': 'יוונית',
    'en': 'אנגלית',
    'es': 'ספרדית',
    'et': 'אסטונית',
    'fa': 'פרסית',
    'fi': 'פינית',
    'fr': 'צרפתית',
    'he': 'עברית',
    'hi': 'הינדי',
    'hr': 'קרואטית',
    'hu': 'הונגרית',
    'id': 'אינדונזית',
    'is': 'איסלנדית',
    'it': 'איטלקית',
    'ja': 'יפנית',
    'ko': 'קוריאנית',
    'lt': 'ליטאית',
    'lv': 'לטבית',
    'ms': 'מלאית',
    'mt': 'מלטזית',
    'nl': 'הולנדית',
    'no': 'נורווגית',
    'pl': 'פולנית',
    'pt': 'פורטוגזית',
    'ro': 'רומנית',
    'ru': 'רוסית',
    'sk': 'סלובקית',
    'sl': 'סלובנית',
    'sv': 'שוודית',
    'sw': 'סווהילי',
    'th': 'תאית',
    'tr': 'טורקית',
    'uk': 'אוקראינית',
    'ur': 'אורדו',
    'vi': 'וייטנאמית',
    'zh-Hans': 'סינית פשוטה',
    'zh-Hant': 'סינית מסורתית'
  }

  const detectLanguage = async (text) => {
    try {
      const response = await fetch(`${process.env.REACT_APP_AZURE_TRANSLATOR_ENDPOINT}/detect?api-version=3.0`, {
        method: 'POST',
        headers: {
          'Ocp-Apim-Subscription-Key': process.env.REACT_APP_AZURE_TRANSLATOR_KEY,
          'Ocp-Apim-Subscription-Region': process.env.REACT_APP_AZURE_REGION,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify([{ text }])
      });

      if (!response.ok) {
        throw new Error('שגיאה בזיהוי השפה');
      }

      const data = await response.json();
      return data[0].language;
    } catch (error) {
      console.error('שגיאה בזיהוי השפה:', error);
      return null;
    }
  }

  const handleFileUpload = async (event) => {
    const file = event.target.files[0]
    if (file) {
      setSelectedFile(file)
      setMessage('')
      setIsLoading(true)
      setTranslatedContent('')
      setDetectedLanguage('')
      
      try {
        const content = await readFileContent(file)
        setFileContent(content)
        
        const detectedLang = await detectLanguage(content)
        if (detectedLang) {
          setDetectedLanguage(detectedLang)
          setMessage(`הקובץ הועלה בהצלחה! שפה מזוהה: ${languages[detectedLang] || detectedLang}`)
        } else {
          setMessage('הקובץ הועלה בהצלחה!')
        }
        
        await uploadFile(file)
      } catch (error) {
        setMessage('שגיאה בהעלאת הקובץ: ' + error.message)
      } finally {
        setIsLoading(false)
      }
    }
  }

  const readFileContent = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader()
      reader.onload = (e) => resolve(e.target.result)
      reader.onerror = (e) => reject(e)
      reader.readAsText(file)
    })
  }

  const handleTranslate = async () => {
    if (!fileContent) return;
    
    setIsLoading(true);
    setTranslationProgress(0);
    
    try {
      const translated = await translateSRT(fileContent, targetLanguage, (progress) => {
        setTranslationProgress(progress);
      });
      
      setTranslatedContent(translated);
      setMessage(`תורגם בהצלחה ל${languages[targetLanguage]}`);
    } catch (error) {
      setMessage('שגיאה בתרגום: ' + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleDownload = () => {
    if (!translatedContent) return;

    const blob = new Blob([translatedContent], { type: 'text/plain;charset=utf-8' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = selectedFile ? `translated_${selectedFile.name}` : 'translated.srt';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const interfaceLanguages = {
    he: 'עברית',
    en: 'English',
    ar: 'العربية',
    ru: 'Русский'
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.currentTarget.classList.add(styles.dragOver);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    e.currentTarget.classList.remove(styles.dragOver);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.currentTarget.classList.remove(styles.dragOver);
    const file = e.dataTransfer.files[0];
    if (file && file.name.endsWith('.srt')) {
      handleFileUpload({ target: { files: [file] } });
    }
  };

  return (
    <motion.div 
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className={`${styles.container} ${isDarkMode ? styles.dark : ''}`}
    >
      <div className={styles.header}>
        <div className={styles.languageToggle}>
          <FiGlobe className={styles.icon} />
          <select
            value={interfaceLanguage}
            onChange={(e) => changeLanguage(e.target.value)}
            className={styles.languageSelector}
          >
            {Object.entries(interfaceLanguages).map(([code, name]) => (
              <option key={code} value={code}>{name}</option>
            ))}
          </select>
        </div>
        
        <motion.button 
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
          onClick={toggleTheme}
          className={styles.themeToggle}
        >
          {isDarkMode ? <FiSun /> : <FiMoon />}
        </motion.button>
      </div>

      <motion.div 
        className={styles.uploadSection}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
        whileHover={{ scale: 1.02 }}
      >
        <input
          ref={fileInputRef}
          type="file"
          accept=".srt"
          onChange={handleFileUpload}
          className={styles.hiddenInput}
        />
        
        <div className={styles.uploadContent} onClick={() => fileInputRef.current.click()}>
          <FiUploadCloud className={styles.uploadIcon} />
          <h3>{t.uploadFile}</h3>
          <p>{t.dragAndDrop}</p>
        </div>
      </motion.div>

      <AnimatePresence>
        {fileContent && (
          <motion.div 
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: 'auto' }}
            exit={{ opacity: 0, height: 0 }}
            className={styles.translationSection}
          >
            {detectedLanguage && (
              <div className={styles.detectedLanguage}>
                <FiGlobe className={styles.icon} />
                {t.detectedLanguage} {languages[detectedLanguage]}
              </div>
            )}
            
            <select
              className={styles.targetLanguageSelect}
              value={targetLanguage}
              onChange={(e) => setTargetLanguage(e.target.value)}
              disabled={isLoading}
            >
              <option value="">{t.selectTargetLanguage}</option>
              {Object.entries(languages)
                .filter(([code]) => code !== detectedLanguage)
                .map(([code, name]) => (
                  <option key={code} value={code}>{name}</option>
                ))}
            </select>

            <motion.button
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              onClick={handleTranslate}
              disabled={isLoading || targetLanguage === detectedLanguage}
              className={styles.translateButton}
            >
              {isLoading ? t.translating : t.translate}
            </motion.button>
          </motion.div>
        )}

        {isLoading && translationProgress > 0 && (
          <motion.div 
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className={styles.progressContainer}
          >
            <div
              className={styles.progressBar}
              style={{ width: `${translationProgress}%` }}
            >
              <span className={styles.progressText}>{translationProgress}%</span>
            </div>
          </motion.div>
        )}

        {message && (
          <motion.div 
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -10 }}
            className={styles.message}
          >
            {message}
          </motion.div>
        )}

        {translatedContent && !isLoading && (
          <motion.button
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
            onClick={handleDownload}
            className={styles.downloadButton}
          >
            <FiDownload className={styles.icon} />
            {t.downloadFile}
          </motion.button>
        )}
      </AnimatePresence>
    </motion.div>
  );
}

export default FileUploader;
