import React, { useState, useRef } from 'react';
import { useApp } from '../context/AppContext';
import styles from './FileUploader.module.css';
import { FiUploadCloud, FiDownload, FiGlobe, FiMoon, FiSun } from 'react-icons/fi';

function FileUploader() {
  const { t, interfaceLanguage, isDarkMode, changeLanguage, toggleTheme } = useApp();
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileContent, setFileContent] = useState('');
  const [targetLanguage, setTargetLanguage] = useState('he');
  const [translatedContent, setTranslatedContent] = useState('');
  const [translationProgress, setTranslationProgress] = useState(0);
  const [detectedLanguage, setDetectedLanguage] = useState('');
  const fileInputRef = useRef(null);

  const languages = {
    'af': 'אפריקאנס',
    'ar': 'ערבית',
    'bg': 'בולגרית',
    'bn': 'בנגלית',
    'bs': 'בוסנית',
    'ca': 'קטלאנית',
    'cs': 'צ׳כית',
    'cy': 'וולשית',
    'da': 'דנית',
    'de': 'גרמנית',
    'el': 'יוונית',
    'en': 'אנגלית',
    'es': 'ספרדית',
    'et': 'אסטונית',
    'fa': 'פרסית',
    'fi': 'פינית',
    'fr': 'צרפתית',
    'he': 'עברית',
    'hi': 'הינדי',
    'hr': 'קרואטית',
    'hu': 'הונגרית',
    'id': 'אינדונזית',
    'is': 'איסלנדית',
    'it': 'איטלקית',
    'ja': 'יפנית',
    'ko': 'קוריאנית',
    'lt': 'ליטאית',
    'lv': 'לטבית',
    'ms': 'מלאית',
    'mt': 'מלטזית',
    'nl': 'הולנדית',
    'no': 'נורווגית',
    'pl': 'פולנית',
    'pt': 'פורטוגזית',
    'ro': 'רומנית',
    'ru': 'רוסית',
    'sk': 'סלובקית',
    'sl': 'סלובנית',
    'sv': 'שוודית',
    'sw': 'סווהילי',
    'th': 'תאית',
    'tr': 'טורקית',
    'uk': 'אוקראינית',
    'ur': 'אורדו',
    'vi': 'וייטנאמית',
    'zh-Hans': 'סינית פשוטה',
    'zh-Hant': 'סינית מסורתית'
  };

  const interfaceLanguages = {
    he: 'עברית',
    en: 'English',
    ar: 'العربية',
    ru: 'Русский'
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.currentTarget.classList.add(styles.dragOver);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    e.currentTarget.classList.remove(styles.dragOver);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.currentTarget.classList.remove(styles.dragOver);
    const file = e.dataTransfer.files[0];
    if (file && file.name.endsWith('.srt')) {
      handleFileUpload({ target: { files: [file] } });
    }
  };

  return (
    <div className={`${styles.container} ${isDarkMode ? styles.dark : ''}`}>
      <div className={styles.header}>
        <div className={styles.languageToggle}>
          <FiGlobe className={styles.icon} />
          <select
            value={interfaceLanguage}
            onChange={(e) => changeLanguage(e.target.value)}
            className={styles.languageSelector}
          >
            {Object.entries(interfaceLanguages).map(([code, name]) => (
              <option key={code} value={code}>{name}</option>
            ))}
          </select>
        </div>
        
        <button 
          onClick={toggleTheme}
          className={styles.themeToggle}
        >
          {isDarkMode ? <FiSun /> : <FiMoon />}
        </button>
      </div>

      <div 
        className={styles.uploadSection}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
      >
        <input
          ref={fileInputRef}
          type="file"
          accept=".srt"
          onChange={handleFileUpload}
          className={styles.hiddenInput}
        />
        
        <div className={styles.uploadContent} onClick={() => fileInputRef.current.click()}>
          <FiUploadCloud className={styles.uploadIcon} />
          <h3>{t.uploadFile}</h3>
          <p>{t.dragAndDrop}</p>
        </div>
      </div>

      {fileContent && (
        <div className={styles.translationSection}>
          {detectedLanguage && (
            <div className={styles.detectedLanguage}>
              <FiGlobe className={styles.icon} />
              {t.detectedLanguage} {languages[detectedLanguage]}
            </div>
          )}
          
          <select
            className={styles.targetLanguageSelect}
            value={targetLanguage}
            onChange={(e) => setTargetLanguage(e.target.value)}
            disabled={isLoading}
          >
            <option value="">{t.selectTargetLanguage}</option>
            {Object.entries(languages)
              .filter(([code]) => code !== detectedLanguage)
              .map(([code, name]) => (
                <option key={code} value={code}>{name}</option>
              ))}
          </select>

          <button
            onClick={handleTranslate}
            disabled={isLoading || targetLanguage === detectedLanguage}
            className={styles.translateButton}
          >
            {isLoading ? t.translating : t.translate}
          </button>
        </div>
      )}

      {isLoading && translationProgress > 0 && (
        <div className={styles.progressContainer}>
          <div
            className={styles.progressBar}
            style={{ width: `${translationProgress}%` }}
          >
            <span className={styles.progressText}>{translationProgress}%</span>
          </div>
        </div>
      )}

      {message && (
        <div className={styles.message}>
          {message}
        </div>
      )}

      {translatedContent && !isLoading && (
        <button
          onClick={handleDownload}
          className={styles.downloadButton}
        >
          <FiDownload className={styles.icon} />
          {t.downloadFile}
        </button>
      )}
    </div>
  );
}

export default FileUploader;
