import { useState, useEffect } from 'react'
import { uploadFile, getFiles, translateSRT } from '../client'
import styles from './FileUploader.module.css'

function FileUploader() {
  const [isLoading, setIsLoading] = useState(false)
  const [message, setMessage] = useState('')
  const [selectedFile, setSelectedFile] = useState(null)
  const [fileContent, setFileContent] = useState('')
  const [targetLanguage, setTargetLanguage] = useState('he')
  const [translatedContent, setTranslatedContent] = useState('')

  const languages = {
    he: 'עברית',
    en: 'אנגלית',
    es: 'ספרדית',
    fr: 'צרפתית',
    de: 'גרמנית',
    ru: 'רוסית',
    ar: 'ערבית'
  }

  const handleFileUpload = async (event) => {
    const file = event.target.files[0]
    if (file) {
      setSelectedFile(file)
      setMessage('')
      setIsLoading(true)
      setTranslatedContent('') // איפוס התרגום הקודם
      
      try {
        // קריאת תוכן הקובץ
        const content = await readFileContent(file)
        setFileContent(content)
        
        // העלאה לשרת
        await uploadFile(file)
        setMessage('הקובץ הועלה בהצלחה!')
      } catch (error) {
        setMessage('שגיאה בהעלאת הקובץ: ' + error.message)
      } finally {
        setIsLoading(false)
      }
    }
  }

  const readFileContent = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader()
      reader.onload = (e) => resolve(e.target.result)
      reader.onerror = (e) => reject(e)
      reader.readAsText(file)
    })
  }

  const handleTranslate = async () => {
    if (!fileContent) return
    
    setIsLoading(true)
    try {
      const translatedContent = await translateSRT(fileContent, targetLanguage)
      setTranslatedContent(translatedContent)
      setMessage(`תורגם בהצלחה ל${languages[targetLanguage]}`)
    } catch (error) {
      setMessage('שגיאה בתרגום: ' + error.message)
    } finally {
      setIsLoading(false)
    }
  }

  const handleDownload = () => {
    if (!translatedContent) return
    
    const blob = new Blob([translatedContent], { type: 'text/plain' })
    const url = URL.createObjectURL(blob)
    const a = document.createElement('a')
    a.href = url
    a.download = `translated_${selectedFile.name}`
    document.body.appendChild(a)
    a.click()
    document.body.removeChild(a)
    URL.revokeObjectURL(url)
  }

  const handleDragOver = (e) => {
    e.preventDefault()
    e.stopPropagation()
  }

  const handleDrop = (e) => {
    e.preventDefault()
    e.stopPropagation()
    
    const file = e.dataTransfer.files[0]
    if (file && file.name.endsWith('.srt')) {
      const input = document.getElementById('file-upload')
      input.files = e.dataTransfer.files
      handleFileUpload({ target: input })
    } else {
      setMessage('אנא העלה קובץ SRT בלבד')
    }
  }

  return (
    <div className={styles.container}>
      <div 
        className={styles.dropzone}
        onDragOver={handleDragOver}
        onDrop={handleDrop}
      >
        <input 
          type="file" 
          accept=".srt" 
          onChange={handleFileUpload}
          id="file-upload"
          className={styles.fileInput}
        />
        <label htmlFor="file-upload" className={styles.uploadButton}>
          {isLoading ? 'מעלה...' : 'בחר או גרור קובץ כתוביות'}
        </label>
        <div className={styles.supportedFormats}>
          קבצי SRT בלבד
        </div>
      </div>

      {message && (
        <div className={message.includes('שגיאה') ? styles.error : styles.success}>
          {message}
        </div>
      )}

      {fileContent && (
        <div className={styles.preview}>
          <h3>תצוגה מקדימה:</h3>
          <div className={styles.previewContent}>
            {fileContent.slice(0, 500)}... {/* מציג רק את 500 התווים הראשונים */}
          </div>
          
          <div className={styles.controls}>
            <select 
              className={styles.languageSelect}
              value={targetLanguage}
              onChange={(e) => setTargetLanguage(e.target.value)}
            >
              {Object.entries(languages).map(([code, name]) => (
                <option key={code} value={code}>
                  {name}
                </option>
              ))}
            </select>
            
            <button 
              className={styles.translateButton}
              onClick={handleTranslate}
              disabled={isLoading}
            >
              {isLoading ? 'מתרגם...' : 'תרגם'}
            </button>
          </div>
        </div>
      )}

      {translatedContent && (
        <div className={styles.preview}>
          <h3>תרגום:</h3>
          <div className={styles.previewContent}>
            {translatedContent.slice(0, 500)}...
          </div>
          <button 
            className={styles.downloadButton}
            onClick={handleDownload}
          >
            הורד קובץ מתורגם
          </button>
        </div>
      )}

      {isLoading && <div className={styles.loader} />}
    </div>
  )
}

export default FileUploader
