import { useState, useEffect } from 'react'
import { uploadFile, getFiles } from '../client'
import styles from './FileUploader.module.css'

function FileUploader() {
  const [isLoading, setIsLoading] = useState(false)
  const [message, setMessage] = useState('')
  const [selectedFile, setSelectedFile] = useState(null)
  const [fileContent, setFileContent] = useState('')
  const [targetLanguage, setTargetLanguage] = useState('en')
  const [translatedContent, setTranslatedContent] = useState('')

  const handleFileUpload = async (event) => {
    const file = event.target.files[0]
    if (file) {
      setSelectedFile(file)
      setMessage('')
      setIsLoading(true)
      
      try {
        // קריאת תוכן הקובץ
        const content = await readFileContent(file)
        setFileContent(content)
        
        // העלאה לשרת
        await uploadFile(file)
        setMessage('הקובץ הועלה בהצלחה!')
      } catch (error) {
        setMessage('שגיאה בהעלאת הקובץ: ' + error.message)
      } finally {
        setIsLoading(false)
      }
    }
  }

  const readFileContent = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader()
      reader.onload = (e) => resolve(e.target.result)
      reader.onerror = (e) => reject(e)
      reader.readAsText(file)
    })
  }

  const handleTranslate = async () => {
    if (!fileContent) return
    
    setIsLoading(true)
    try {
      // כאן נוסיף את הלוגיקה של התרגום
      // לדוגמה, שימוש ב-API של תרגום
      setTranslatedContent('תוכן מתורגם יופיע כאן...')
    } catch (error) {
      setMessage('שגיאה בתרגום: ' + error.message)
    } finally {
      setIsLoading(false)
    }
  }

  return (
    <div className={styles.container}>
      <div className={styles.dropzone}>
        <input 
          type="file" 
          accept=".srt" 
          onChange={handleFileUpload}
          id="file-upload"
          className={styles.fileInput}
        />
        <label htmlFor="file-upload" className={styles.uploadButton}>
          {isLoading ? 'מעלה...' : 'בחר קובץ כתוביות'}
        </label>
      </div>

      {message && (
        <div className={message.includes('שגיאה') ? styles.error : styles.success}>
          {message}
        </div>
      )}

      {fileContent && (
        <div className={styles.preview}>
          <h3>תצוגה מקדימה:</h3>
          <div className={styles.previewContent}>
            {fileContent.slice(0, 500)}... {/* מציג רק את 500 התווים הראשונים */}
          </div>
          
          <div className={styles.controls}>
            <select 
              className={styles.languageSelect}
              value={targetLanguage}
              onChange={(e) => setTargetLanguage(e.target.value)}
            >
              <option value="en">אנגלית</option>
              <option value="es">ספרדית</option>
              <option value="fr">צרפתית</option>
              <option value="de">גרמנית</option>
            </select>
            
            <button 
              className={styles.translateButton}
              onClick={handleTranslate}
              disabled={isLoading}
            >
              תרגם
            </button>
          </div>
        </div>
      )}

      {translatedContent && (
        <div className={styles.preview}>
          <h3>תרגום:</h3>
          <div className={styles.previewContent}>
            {translatedContent}
          </div>
        </div>
      )}

      {isLoading && <div className={styles.loader} />}
    </div>
  )
}

export default FileUploader
