import { useState } from 'react'
import { uploadFile } from '../client'
import styles from './FileUploader.module.css'

function FileUploader() {
  const [isLoading, setIsLoading] = useState(false)
  const [message, setMessage] = useState('')
  const [selectedFile, setSelectedFile] = useState(null)

  const handleFileUpload = async (event) => {
    const file = event.target.files[0]
    if (file) {
      setSelectedFile(file)
      setMessage('')
      setIsLoading(true)
      
      try {
        const result = await uploadFile(file)
        setMessage('הקובץ הועלה בהצלחה!')
        console.log('הקובץ הועלה בהצלחה:', result)
      } catch (error) {
        setMessage('שגיאה בהעלאת הקובץ: ' + error.message)
        console.error('שגיאה בהעלאת הקובץ:', error)
      } finally {
        setIsLoading(false)
      }
    }
  }

  return (
    <div className={styles.container}>
      <input 
        type="file" 
        accept=".srt" 
        onChange={handleFileUpload}
        id="file-upload"
        className={styles.fileInput}
      />
      <label htmlFor="file-upload" className={styles.uploadButton}>
        {isLoading ? 'מעלה...' : 'בחר קובץ SRT'}
      </label>
      
      {selectedFile && !isLoading && (
        <div className={styles.fileName}>
          {selectedFile.name}
        </div>
      )}
      
      {isLoading && (
        <div className={styles.loader}></div>
      )}
      
      {message && (
        <div className={message.includes('שגיאה') ? styles.error : styles.success}>
          {message}
        </div>
      )}
    </div>
  )
}

export default FileUploader
