import { supabase } from './supabaseClient'

// פונקציה לפירוק קובץ SRT
const parseSRT = (content) => {
  const blocks = content.trim().split('\n\n')
  return blocks.map(block => {
    const [index, timeCode, ...textLines] = block.split('\n')
    return {
      index: parseInt(index),
      timeCode,
      text: textLines.join('\n')
    }
  })
}

// פונקציה להרכבת קובץ SRT
const buildSRT = (blocks) => {
  return blocks.map(block => {
    return `${block.index}\n${block.timeCode}\n${block.text}`
  }).join('\n\n')
}

// פונקציה לתרגום טקסט
const translateText = async (text, targetLang) => {
  const response = await fetch('https://translation.googleapis.com/language/translate/v2', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${process.env.REACT_APP_GOOGLE_API_KEY}`
    },
    body: JSON.stringify({
      q: text,
      target: targetLang
    })
  })

  if (!response.ok) {
    throw new Error('שגיאה בתרגום')
  }

  const data = await response.json()
  return data.data.translations[0].translatedText
}

export const translateSRT = async (content, targetLang) => {
  try {
    // פירוק הקובץ לבלוקים
    const blocks = parseSRT(content)
    
    // תרגום כל בלוק
    const translatedBlocks = await Promise.all(
      blocks.map(async (block) => {
        const translatedText = await translateText(block.text, targetLang)
        return {
          ...block,
          text: translatedText
        }
      })
    )
    
    // הרכבת הקובץ מחדש
    return buildSRT(translatedBlocks)
  } catch (error) {
    console.error('שגיאה בתרגום:', error)
    throw error
  }
}

export const uploadFile = async (file) => {
  try {
    const fileName = `${Date.now()}-${file.name}`
    const { data: storageData, error: storageError } = await supabase.storage
      .from('srt-files')
      .upload(fileName, file)
    
    if (storageError) throw storageError

    const { data: fileData, error: dbError } = await supabase
      .from('files')
      .insert([
        { 
          name: file.name,
          storage_path: storageData.path,
          size: file.size,
          created_at: new Date()
        }
      ])
      .select()
    
    if (dbError) throw dbError
    return fileData[0]
  } catch (error) {
    console.error('שגיאה בהעלאת הקובץ:', error.message)
    throw error
  }
}

export const getFiles = async () => {
  try {
    const { data, error } = await supabase
      .from('files')
      .select('*')
      .order('created_at', { ascending: false })
    
    if (error) throw error
    return data
  } catch (error) {
    console.error('שגיאה בקבלת הקבצים:', error.message)
    throw error
  }
}
