import React, { useState } from 'react'
import { uploadFile, translateSRT } from '../client'
import SubtitlePreview from './SubtitlePreview'
import styles from './FileUploader.module.css'

function FileUploader() {
  const [isLoading, setIsLoading] = useState(false)
  const [message, setMessage] = useState('')
  const [selectedFile, setSelectedFile] = useState(null)
  const [fileContent, setFileContent] = useState('')
  const [targetLanguage, setTargetLanguage] = useState('en')
  const [translatedContent, setTranslatedContent] = useState('')
  const [translationProgress, setTranslationProgress] = useState(0)

  const languages = {
    he: 'עברית',
    en: 'אנגלית',
    es: 'ספרדית',
    fr: 'צרפתית',
    de: 'גרמנית',
    ru: 'רוסית',
    ar: 'ערבית'
  }

  const handleFileUpload = async (event) => {
    const file = event.target.files[0]
    if (file) {
      setSelectedFile(file)
      setMessage('')
      setIsLoading(true)
      
      try {
        // קריאת תוכן הקובץ
        const content = await readFileContent(file)
        setFileContent(content)
        
        // העלאה לשרת
        await uploadFile(file)
        setMessage('הקובץ הועלה בהצלחה!')
      } catch (error) {
        setMessage('שגיאה בהעלאת הקובץ: ' + error.message)
      } finally {
        setIsLoading(false)
      }
    }
  }

  const readFileContent = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader()
      reader.onload = (e) => resolve(e.target.result)
      reader.onerror = (e) => reject(e)
      reader.readAsText(file)
    })
  }

  const handleTranslate = async () => {
    if (!fileContent) return;
    
    setIsLoading(true);
    setTranslationProgress(0);
    
    try {
      const translated = await translateSRT(fileContent, targetLanguage, (progress) => {
        setTranslationProgress(progress);
      });
      
      setTranslatedContent(translated);
      setMessage(`תורגם בהצלחה ל${languages[targetLanguage]}`);
    } catch (error) {
      setMessage('שגיאה בתרגום: ' + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className={styles.container}>
      <div className={styles.dropzone}>
        <input 
          type="file" 
          accept=".srt" 
          onChange={handleFileUpload}
          id="file-upload"
          className={styles.fileInput}
        />
        <label htmlFor="file-upload" className={styles.uploadButton}>
          {isLoading ? 'מעלה...' : 'בחר קובץ כתוביות'}
        </label>
      </div>

      {message && (
        <div className={message.includes('שגיאה') ? styles.error : styles.success}>
          {message}
        </div>
      )}

      {fileContent && (
        <>
          <SubtitlePreview content={fileContent} isTranslated={false} />
          
          <div className={styles.controls}>
            <select 
              className={styles.languageSelect}
              value={targetLanguage}
              onChange={(e) => setTargetLanguage(e.target.value)}
            >
              {Object.entries(languages).map(([code, name]) => (
                <option key={code} value={code}>
                  {name}
                </option>
              ))}
            </select>
            
            <button 
              className={styles.translateButton}
              onClick={handleTranslate}
              disabled={isLoading}
            >
              {isLoading ? 'מתרגם...' : 'תרגם'}
            </button>
          </div>
        </>
      )}

      {translatedContent && (
        <SubtitlePreview content={translatedContent} isTranslated={true} />
      )}
    </div>
  )
}

export default FileUploader
