export const azureConfig = {
    key: process.env.REACT_APP_AZURE_KEY,
    endpoint: "https://api.cognitive.microsofttranslator.com/",
    location: process.env.REACT_APP_AZURE_REGION
};

if (!azureConfig.key) {
  console.error('Azure API key is missing! Make sure REACT_APP_AZURE_KEY is set in .env file');
}

if (!azureConfig.location) {
  console.error('Azure region is missing! Make sure REACT_APP_AZURE_REGION is set in .env file');
}
