import React, { useState, useEffect } from 'react'
import { uploadFile } from '../client'
import SubtitlePreview from './SubtitlePreview'
import styles from './FileUploader.module.css'
import { translateSRT } from '../client'
import { useApp } from '../context/AppContext'

function FileUploader() {
  const { t, interfaceLanguage, isDarkMode, changeLanguage, toggleTheme } = useApp()
  const [isLoading, setIsLoading] = useState(false)
  const [message, setMessage] = useState('')
  const [selectedFile, setSelectedFile] = useState(null)
  const [fileContent, setFileContent] = useState('')
  const [targetLanguage, setTargetLanguage] = useState('he')
  const [translatedContent, setTranslatedContent] = useState('')
  const [translationProgress, setTranslationProgress] = useState(0)
  const [detectedLanguage, setDetectedLanguage] = useState('')

  const languages = {
    'af': '◊ê◊§◊®◊ô◊ß◊ê◊†◊°',
    'ar': '◊¢◊®◊ë◊ô◊™',
    'bg': '◊ë◊ï◊ú◊í◊®◊ô◊™',
    'bn': '◊ë◊†◊í◊ú◊ô◊™',
    'bs': '◊ë◊ï◊°◊†◊ô◊™',
    'ca': '◊ß◊ò◊ú◊ê◊†◊ô◊™',
    'cs': '◊¶◊≥◊õ◊ô◊™',
    'cy': '◊ï◊ï◊ú◊©◊ô◊™',
    'da': '◊ì◊†◊ô◊™',
    'de': '◊í◊®◊û◊†◊ô◊™',
    'el': '◊ô◊ï◊ï◊†◊ô◊™',
    'en': '◊ê◊†◊í◊ú◊ô◊™',
    'es': '◊°◊§◊®◊ì◊ô◊™',
    'et': '◊ê◊°◊ò◊ï◊†◊ô◊™',
    'fa': '◊§◊®◊°◊ô◊™',
    'fi': '◊§◊ô◊†◊ô◊™',
    'fr': '◊¶◊®◊§◊™◊ô◊™',
    'he': '◊¢◊ë◊®◊ô◊™',
    'hi': '◊î◊ô◊†◊ì◊ô',
    'hr': '◊ß◊®◊ï◊ê◊ò◊ô◊™',
    'hu': '◊î◊ï◊†◊í◊®◊ô◊™',
    'id': '◊ê◊ô◊†◊ì◊ï◊†◊ñ◊ô◊™',
    'is': '◊ê◊ô◊°◊ú◊†◊ì◊ô◊™',
    'it': '◊ê◊ô◊ò◊ú◊ß◊ô◊™',
    'ja': '◊ô◊§◊†◊ô◊™',
    'ko': '◊ß◊ï◊®◊ô◊ê◊†◊ô◊™',
    'lt': '◊ú◊ô◊ò◊ê◊ô◊™',
    'lv': '◊ú◊ò◊ë◊ô◊™',
    'ms': '◊û◊ú◊ê◊ô◊™',
    'mt': '◊û◊ú◊ò◊ñ◊ô◊™',
    'nl': '◊î◊ï◊ú◊†◊ì◊ô◊™',
    'no': '◊†◊ï◊®◊ï◊ï◊í◊ô◊™',
    'pl': '◊§◊ï◊ú◊†◊ô◊™',
    'pt': '◊§◊ï◊®◊ò◊ï◊í◊ñ◊ô◊™',
    'ro': '◊®◊ï◊û◊†◊ô◊™',
    'ru': '◊®◊ï◊°◊ô◊™',
    'sk': '◊°◊ú◊ï◊ë◊ß◊ô◊™',
    'sl': '◊°◊ú◊ï◊ë◊†◊ô◊™',
    'sv': '◊©◊ï◊ï◊ì◊ô◊™',
    'sw': '◊°◊ï◊ï◊î◊ô◊ú◊ô',
    'th': '◊™◊ê◊ô◊™',
    'tr': '◊ò◊ï◊®◊ß◊ô◊™',
    'uk': '◊ê◊ï◊ß◊®◊ê◊ô◊†◊ô◊™',
    'ur': '◊ê◊ï◊®◊ì◊ï',
    'vi': '◊ï◊ô◊ô◊ò◊†◊ê◊û◊ô◊™',
    'zh-Hans': '◊°◊ô◊†◊ô◊™ ◊§◊©◊ï◊ò◊î',
    'zh-Hant': '◊°◊ô◊†◊ô◊™ ◊û◊°◊ï◊®◊™◊ô◊™'
  }

  const detectLanguage = async (text) => {
    try {
      const response = await fetch(`${process.env.REACT_APP_AZURE_TRANSLATOR_ENDPOINT}/detect?api-version=3.0`, {
        method: 'POST',
        headers: {
          'Ocp-Apim-Subscription-Key': process.env.REACT_APP_AZURE_TRANSLATOR_KEY,
          'Ocp-Apim-Subscription-Region': process.env.REACT_APP_AZURE_REGION,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify([{ text }])
      });

      if (!response.ok) {
        throw new Error('◊©◊í◊ô◊ê◊î ◊ë◊ñ◊ô◊î◊ï◊ô ◊î◊©◊§◊î');
      }

      const data = await response.json();
      return data[0].language;
    } catch (error) {
      console.error('◊©◊í◊ô◊ê◊î ◊ë◊ñ◊ô◊î◊ï◊ô ◊î◊©◊§◊î:', error);
      return null;
    }
  }

  const handleFileUpload = async (event) => {
    const file = event.target.files[0]
    if (file) {
      setSelectedFile(file)
      setMessage('')
      setIsLoading(true)
      setTranslatedContent('')
      setDetectedLanguage('')
      
      try {
        const content = await readFileContent(file)
        setFileContent(content)
        
        const detectedLang = await detectLanguage(content)
        if (detectedLang) {
          setDetectedLanguage(detectedLang)
          setMessage(`◊î◊ß◊ï◊ë◊• ◊î◊ï◊¢◊ú◊î ◊ë◊î◊¶◊ú◊ó◊î! ◊©◊§◊î ◊û◊ñ◊ï◊î◊î: ${languages[detectedLang] || detectedLang}`)
        } else {
          setMessage('◊î◊ß◊ï◊ë◊• ◊î◊ï◊¢◊ú◊î ◊ë◊î◊¶◊ú◊ó◊î!')
        }
        
        await uploadFile(file)
      } catch (error) {
        setMessage('◊©◊í◊ô◊ê◊î ◊ë◊î◊¢◊ú◊ê◊™ ◊î◊ß◊ï◊ë◊•: ' + error.message)
      } finally {
        setIsLoading(false)
      }
    }
  }

  const readFileContent = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader()
      reader.onload = (e) => resolve(e.target.result)
      reader.onerror = (e) => reject(e)
      reader.readAsText(file)
    })
  }

  const handleTranslate = async () => {
    if (!fileContent) return;
    
    setIsLoading(true);
    setTranslationProgress(0);
    
    try {
      const translated = await translateSRT(fileContent, targetLanguage, (progress) => {
        setTranslationProgress(progress);
      });
      
      setTranslatedContent(translated);
      setMessage(`◊™◊ï◊®◊í◊ù ◊ë◊î◊¶◊ú◊ó◊î ◊ú${languages[targetLanguage]}`);
    } catch (error) {
      setMessage('◊©◊í◊ô◊ê◊î ◊ë◊™◊®◊í◊ï◊ù: ' + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleDownload = () => {
    if (!translatedContent) return;

    const blob = new Blob([translatedContent], { type: 'text/plain;charset=utf-8' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = selectedFile ? `translated_${selectedFile.name}` : 'translated.srt';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const interfaceLanguages = {
    he: '◊¢◊ë◊®◊ô◊™',
    en: 'English',
    ar: 'ÿßŸÑÿπÿ±ÿ®Ÿäÿ©',
    ru: '–†—É—Å—Å–∫–∏–π'
  };

  return (
    <div className={`${styles.container} ${isDarkMode ? styles.dark : ''}`}>
      <div className={styles.header}>
        <select
          value={interfaceLanguage}
          onChange={(e) => changeLanguage(e.target.value)}
          className={styles.languageSelector}
        >
          {Object.entries(interfaceLanguages).map(([code, name]) => (
            <option key={code} value={code}>{name}</option>
          ))}
        </select>
        
        <button 
          onClick={toggleTheme}
          className={styles.themeToggle}
        >
          {isDarkMode ? '‚òÄÔ∏è' : 'üåô'}
        </button>
      </div>

      <div className={styles.uploadSection}>
        <label className={styles.fileInput}>
          <input
            type="file"
            accept=".srt"
            onChange={handleFileUpload}
            disabled={isLoading}
          />
          <span>{t.uploadFile}</span>
        </label>
      </div>

      {fileContent && (
        <div className={styles.translationSection}>
          {detectedLanguage && (
            <div className={styles.detectedLanguage}>
              {t.detectedLanguage} {languages[detectedLanguage]}
            </div>
          )}
          
          <select
            className={styles.targetLanguageSelect}
            value={targetLanguage}
            onChange={(e) => setTargetLanguage(e.target.value)}
            disabled={isLoading}
          >
            <option value="">{t.selectTargetLanguage}</option>
            {Object.entries(languages)
              .filter(([code]) => code !== detectedLanguage)
              .map(([code, name]) => (
                <option key={code} value={code}>{name}</option>
              ))}
          </select>

          <button
            onClick={handleTranslate}
            disabled={isLoading || targetLanguage === detectedLanguage}
            className={styles.translateButton}
          >
            {isLoading ? t.translating : t.translate}
          </button>
        </div>
      )}

      {isLoading && translationProgress > 0 && (
        <div className={styles.progressContainer}>
          <div
            className={styles.progressBar}
            style={{ width: `${translationProgress}%` }}
          >
            {translationProgress}%
          </div>
        </div>
      )}

      {message && (
        <div className={`${styles.message} ${styles.fadeIn}`}>
          {message}
        </div>
      )}

      {translatedContent && !isLoading && (
        <button
          onClick={handleDownload}
          className={`${styles.downloadButton} ${styles.fadeIn}`}
        >
          {t.downloadFile}
        </button>
      )}

      {translatedContent && (
        <SubtitlePreview content={translatedContent} />
      )}
    </div>
  )
}

export default FileUploader
